package org.lili.security;

import org.junit.Assert;
import org.junit.Test;


public class AesUtilsTest {

    @Test
    public void codeJiami() {
        String content = "269c4a09af161b81e1c2d0c9d0fa8762";
        System.out.println("加密前：" + content);
        String encryptResult = AesUtils.codeJiami(content);
        System.out.println("加密后：" + encryptResult);
        String decryptResult = AesUtils.codeJiemi("21be70f7326525c25d40346aa4232b53");
        System.out.println("解密后：" + decryptResult);
    }

    @Test
    public void test() {
        try {
            //String pwd = "0123456789012345";
            String pwd = "abcdefg$6&*~aaaa";
            String errorPwd = "aaaa4";
            String content = "mysql innodb is fun";
            String mysqlInnodbIsFun = AesUtils.encrypt(content, pwd);
            String decryptContent = AesUtils.decrypt(mysqlInnodbIsFun, pwd);
            Assert.assertEquals(content, decryptContent);
            String decryptError = AesUtils.decrypt(mysqlInnodbIsFun, errorPwd);
            System.out.println(decryptError);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    @Test
    public void encrptyMyPrivateKey() throws Exception {
        String financeContent = "";
        String pwd = "easy";
        String encrptyContent = AesUtils.encrypt(financeContent, pwd);
        System.out.println(encrptyContent);

        String walletContent = "";
        String encrptyWalletContent = AesUtils.encrypt(walletContent, pwd);
        System.out.println(encrptyWalletContent);
    }



    @Test
    public void encrptyPrivateKey() throws Exception {
        String financeContent = "";
        //String pwd = "{1q2w3e4r}";
        String pwd = "{1234qwer}";
        String encrptyContent = AesUtils.decrypt(financeContent, pwd);
        System.out.println(encrptyContent);
        System.out.println("----------------------------------");
        String walletContent = "";
        String encrptyWalletContent = AesUtils.decrypt(walletContent, pwd);
        System.out.println(encrptyWalletContent);
    }
}